import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import PropAtelier from "../propAtelier/propAtelier";
import NewAtelier from "../newAtelier/NewAtelier.js";

class Dashboard extends Component {


onLogoutClick = e => {
  e.preventDefault();
  this.props.logoutUser();
};
  render() {
    console.log('localStorage.local sur dashbord'+localStorage.id);

    const { user } = this.props.auth;

    return (
      <div>
      <div style={{ height: "75vh" }} className="container valign-wrapper">
        <div className="container-fluid">

      </div>
        <div className="row">
          <div className="landing-copy col s12 center-align">
            <h4>
              <b></b> {user.nom.split(" ")[0]}
              <p className="flow-text grey-text text-darken-1">
                     BIENVENU    {" "}
                <span style={{ fontFamily: "monospace" }}></span> üëè

              </p>
            </h4>
            <button
              style={{
                width: "150px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "12rem"

              }}
              onClick={this.onLogoutClick}
              className="btn btn-large waves-effect waves-light hoverable blue accent-3"
            >
              Deconnecter
            </button>
          </div>
        </div>
      </div>
      <NewAtelier/>
      <PropAtelier/>
      </div>
    );
  }
}

Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { logoutUser }
)(Dashboard);
